  import {add, getDict, dictionary, remove, update} from "@/api/system/dict";
  import Question from "./question";
  import {mapGetters} from "vuex";

  export default {
    data() {
      return {
        questionType:{},
        form: {},
        selectionList: [],
        questionPain: {title: "", visible: false},
        loading: true,
        option: {
          tip: false,
          border: true,
          selection: true,
          menuWidth: 300,
          column: [
            {
              label: "问题分类",
              prop: "dictValue",
              rules: [{
                required: true,
                message: "请输入问题分类",
                trigger: "blur"
              }]
            },
            {
              label: "排序",
              prop: "sort",
              type: "number",
              rules: [{
                required: true,
                message: "请输入字典排序",
                trigger: "blur"
              }]
            }
          ]
        },
        data: []
      };
    },
    components: {
      Question
    },
    computed: {
      ...mapGetters(["permission"]),
      permissionList() {
        return {
          addBtn: this.vaildData(this.permission.question_add, false),
          viewBtn: this.vaildData(this.permission.question_view, false),
          delBtn: this.vaildData(this.permission.question_delete, false),
          editBtn: this.vaildData(this.permission.question_edit, false)
        };
      },
      ids() {
        let ids = [];
        this.selectionList.forEach(ele => {
          ids.push(ele.id);
        });
        return ids.join(",");
      }
    },
    methods: {
      rowSave(row, done, loading) {
        row.code = "question_type";
        row.parentId = "1585202623076737025";
        add(row).then(() => {
          done();
          this.onLoad(this.page);
          this.$message({
            type: "success",
            message: "操作成功!"
          });
        }, error => {
          window.console.log(error);
          loading();
        });
      },
      rowUpdate(row, index, done, loading) {
        update(row).then(() => {
          done();
          this.onLoad(this.page);
          this.$message({
            type: "success",
            message: "操作成功!"
          });
        }, error => {
          window.console.log(error);
          loading();
        });
      },
      rowDel(row) {
        this.$confirm("确定将选择数据删除?", {
          confirmButtonText: "确定",
          cancelButtonText: "取消",
          type: "warning"
        })
          .then(() => {
            return remove(row.id);
          })
          .then(() => {
            this.onLoad(this.page);
            this.$message({
              type: "success",
              message: "操作成功!"
            });
          });
      },
      searchReset() {
        this.onLoad();
      },
      searchChange(params, done) {
        this.onLoad();
        done();
      },
      selectionChange(list) {
        this.selectionList = list;
      },
      handleDelete() {
        if (this.selectionList.length === 0) {
          this.$message.warning("请选择至少一条数据");
          return;
        }
        this.$confirm("确定将选择数据删除?", {
          confirmButtonText: "确定",
          cancelButtonText: "取消",
          type: "warning"
        })
          .then(() => {
            return remove(this.ids);
          })
          .then(() => {
            this.onLoad(this.page);
            this.$message({
              type: "success",
              message: "操作成功!"
            });
            this.$refs.crud.toggleSelection();
          });
      },
      beforeOpen(done, type) {
        if (["edit", "view"].includes(type)) {
          getDict(this.form.id).then(res => {
            this.form = res.data.data;
          });
        }
        done();
      },
      beforeCloseDialog(done) {
        done();
      },
      showDialogPage(row, type) {
        this.questionType = row;
        if (type == 'question') {
          this.questionPain.visible = true;
          this.questionPain.title = row.dictValue + " - 问题列表";
        }
      },
      onLoad() {
        this.loading = true;
        dictionary("question_type").then(res => {
          this.data = res.data.data;
          this.loading = false;
        });
      }
    }
  };
