import {mapGetters} from "vuex";
import {
  order_first_trial,
  order_note_list,
} from "../../components/order/order_dialog";
import param from "../system/param";
import order_button from "../../components/order/order_button";
import {examines} from "../../util/order";
import {getWaitingExamineList, takeAuditTask, taskNumber} from "../../api/audit/orderexamine";
import order_list_item from "../../components/order/order_list_item";
import moment from "moment";

export default {
  components: {
    order_button,order_list_item
  },
  data() {
    return {
      form: {},
      query: {},
      table_key:0,
      loading: true,
      taskNumber: 0,
      dialogVisible:false,
      orderLabels:["no","userInfo","goodsInfo","rentInfo","depositMoney","note","danger"],
      page: {
        pageSize: 10,
        currentPage: 1,
        total: 0
      },
      selectionList: [],
      option: {
        height: 'auto',
        calcHeight: 210,
        searchShow: true,
        searchMenuSpan: 6,
        tip: false,
        border: true,
        delBtn:false,
        addBtn: false,
        index: false,
        viewBtn: false,
        editBtn:false,
        selection: true,
        menuWidth:200,
        column: [
          {
            label: "订单编号",
            prop: "no",
            width:200,
            slot: true,
            search:true,
            searchSpan:4,
            searchOrder:1,
          },
          {
            label: "用户姓名",
            prop: "userName",
            searchSpan:4,
            slot: true,
            hide: true,
            search:true,
          },
          {
            label: "手机号",
            prop: "phone",
            width:230,
            searchSpan:4,
            slot: true,
            hide: true,
            search:true,
          },
          {
            label: "商品编号",
            prop: "goodsId",
            width:230,
            searchSpan:4,
            slot: true,
            hide: true,
            search:true,
          },
          {
            label: "商品名称",
            prop: "goodsTitle",
            width:230,
            searchSpan:4,
            slot: true,
            hide: true,
            search:true,
          },
          {
            label: '下单时间',
            prop: 'createTime',
            type: 'daterange',
            searchSpan:8,
            searchRange:true,
            format:'yyyy-MM-dd',
            valueFormat:'yyyy-MM-dd',
            hide:true,
            search:true,
          },
          {
            label: '租期开始',
            prop: 'rentTime',
            type: 'daterange',
            format:'yyyy-MM-dd',
            valueFormat:'yyyy-MM-dd',
            searchSpan:8,
            searchRange:true,
            hide:true,
            search:true,
          },
          {
            label: "用户信息",
            prop: "userInfo",
            width:240,
          },
          {
            label: "商品信息",
            prop: "goodsInfo",
            width:160,
          },
          {
            label: "租赁方案",
            prop: "rentInfo",
            width:360,
          },
          {
            label: "押金",
            prop: "depositMoney",
            width:240,
          },
          {
            label: "渠道来源",
            prop: "channel",
            width:120,
          },
          {
            label: "风控/征信",
            width:240,
            prop: "danger",
          },
          {
            label: "最新备注",
            width:120,
            prop: "note",
          },

        ]
      },
      data: [],
      fn:{start:this.start,end:this.end}
    };
  },
  computed: {
    ...mapGetters(["userInfo","permission"]),
    permissionList() {
      return {
        addBtn: this.vaildData(this.permission.orderexamine_add, false),
        viewBtn: this.vaildData(this.permission.orderexamine_view, false),
        delBtn: this.vaildData(this.permission.orderexamine_delete, false),
        editBtn: this.vaildData(this.permission.orderexamine_edit, false)
      };
    },
    ids() {
      let ids = [];
      this.selectionList.forEach(ele => {
        ids.push(ele.id);
      });
      return ids.join(",");
    },
    btn(){
      console.log("------------------------")
      console.log(this.userInfo.authority,this.userInfo.authority.includes("auditor_director")||this.userInfo.authority.includes("auditor"))
      return this.userInfo.authority.includes("auditor_director")||this.userInfo.authority.includes("auditor");
    },
  },
  methods: {
    beforeCloseDialog(done) {
      done();
    },
    start() {

    },
    getAuditTask(){
      takeAuditTask(30).then(res => {
        this.$message(res.data.msg);
        this.table_key = new Date().getTime();
        this.dialogVisible = false;
      })
    },
    end(key){
      this.table_key = new Date().getTime();
    },
    getTask(){
      this.dialogVisible = true
    },
    searchReset() {
      this.query = {};
      this.onLoad(this.page);
    },
    searchChange(params, done) {
      this.query = params;
      this.page.currentPage = 1;
      this.onLoad(this.page, params);
      done();
    },
    selectionChange(list) {
      this.selectionList = list;
    },
    selectionClear() {
      this.selectionList = [];
      this.$refs.crud.toggleSelection();
    },
    currentChange(currentPage){
      this.page.currentPage = currentPage;
    },
    sizeChange(pageSize){
      this.page.pageSize = pageSize;
    },
    onLoad(page, params = {}) {
      console.log(this.userInfo)
      this.loading = true;
      // params.examineFirst = 1;
      if (this.query.createTime&&this.query.createTime.length == 2){
        this.query.beginCreateTime = moment(this.query.createTime[0]+" 00:00:00").format('YYYY-MM-DD HH:mm:ss');
        this.query.endCreateTime = moment(this.query.createTime[1]+" 23:59:59").format('YYYY-MM-DD HH:mm:ss');
        delete this.query.createTime;
      }

      if (this.query.rentTime&&this.query.rentTime.length == 2){
        this.query.beginRentTime = this.query.rentTime[0];
        this.query.endRentTime = this.query.rentTime[1];
        delete this.query.rentTime;
      }
      getWaitingExamineList(page.currentPage, page.pageSize, Object.assign(params, this.query)).then(res => {
        const data = res.data.data;
        this.page.total = data.total;
        this.data = data.records;
        this.loading = false;
        this.selectionClear();
      });
      taskNumber().then(res => {
        this.taskNumber = res.data.data;
      })
    }
  }
};
