result:
.top-bar-title {
  height: auto;
  background-color: white;
  padding: 0px 10px 10px 20px;
  padding-bottom: 0px !important;
  margin-left: 6px;
  margin-right: 6px;
  display: block;
  justify-content: space-between;
  border-bottom: 2px solid #F0F2F5;
}
.top_item{
  margin-right: 20px;
}
:deep .el-tabs__header {
  margin: 0 0 0px !important;
}
.top_contain {
  font-size: 14px;
  display: flex;
  justify-content: flex-start;
  flex-direction: row;
  flex-wrap: wrap;
  align-items: flex-end;
  margin-top: 10px;
  margin-bottom: 10px;
}

:deep .head-class {
  background-color: white !important;
}
.info {
  margin-top: 5px;
  margin-left: 6px;
  margin-right: 6px;
}

.info  :deep .clearfix:before {
  content: unset !important;
}
.info  :deep .clearfix:after{
  content: unset !important;
}


.info .text {
  font-size: 14px;
}

.info .item {
  margin-bottom: 18px;
}

:deep .el-card__header {
  padding: 18px 20px !important;
}

.info .clearfix:before,
.info .clearfix:after {
  display: table;
  content: "";
}

.top :deep .el-form-item {
  margin-bottom: 0px;
}

.top :deep .el-col {
  margin-bottom: 0px;
}

.user_info :deep .el-form-item {
  margin-bottom: 0px;
}

.user_info .logistics {
  padding: 5px 10px;
  font-size: 14px;
}

.user_info :deep .el-col {
  margin-bottom: 0px;
}

.info .clearfix:after {
  clear: both
}
:deep .el-tabs__nav-wrap::after{
  background-color: #F0F2F5;
}
.user_info table{
  font-size: 14px;
  width: 100%;
  border-top: 1px solid #00000015;
  border-left: 1px solid #00000015;
  border-collapse: collapse; /*设置单元格的边框合并为1*/
}
.user_info td{
  width: 33%;
  border-bottom: 1px solid #00000015;
  border-right: 1px solid #00000015;
  padding: 10px 20px;
}

:deep .avue-crud__menu {
  display: none;
}

:deep .head-class {
  background-color: white !important;
}
.div_table {
  width: 100%;
}

.table_border{
  border-right: 1px solid #E9E9E9;
  border-bottom: 1px solid #E9E9E9;
}

.div_header {
  background-color: #FAFAFA;
  color: #108EE9;
  padding: 10px 20px;
  font-size: 14px;
  font-weight: bold;
  border-top: 1px solid #E9E9E9;
  border-left: 1px solid #E9E9E9;
}
.div_top {
  width: 100%;
  background-color: #169BD533;
}
.div_top .div_item {
  flex: 1;
  padding-top: 8px;
  padding-bottom: 8px;
  text-align: center;
  font-size: 14px;
}

.div_content {
  width: 100%;
  display: flex;
  flex-direction: row;
  border-left: 1px solid #E9E9E9;
}

.div_content .div_item {
  text-align: center;
  font-size: 14px;
}
:deep .el-empty {
  padding: 0px 0px !important;
}
:deep .avue-crud__empty {
  padding: 0px 0px !important;
}
